import Header from "./components/Header";
import Events from "./components/Events";
import { useState } from "react";
import { Nav, Navbar, NavDropdown, NavLink } from "react-bootstrap";
import "bootstrap/dist/css/bootstrap.min.css";
import { Link, Route, Router, Switch } from "react-router-dom";
import AddEvent from "./components/AddEvent";
import MyEvents from "./components/MyEvents";
import PastEvent from "./components/PastEvent";
import UpcomingEvent from "./components/UpcomingEvent";

function App() {
  const [showAddEvent, setShowAddEvent] = useState(false);
  const [events, setEvents] = useState([
    {
      id: 1,
      text: "Birthday event",
      organiser: "snaksk",
      location: "sss",
      date: "abcd",
      description: "sss",
      registered: false,
    },
    {
      id: 2,
      text: "Anniversary event",
      organiser: "snaksk",
      location: "sss",
      date: "abcd",
      description: "sss",
      registered: true,
    },
    {
      id: 3,
      text: "Birthday event",
      organiser: "snaksk",
      location: "sss",
      date: "abcd",
      description: "sss",
      registered: false,
    },
    {
      id: 4,
      text: "Birthday event",
      organiser: "snaksk",
      location: "sss",
      date: "abcd",
      description: "sss",
      registered: false,
    },
    {
      id: 5,
      text: "Birthday event",
      organiser: "snaksk",
      location: "sss",
      date: "abcd",
      description: "sss",
      registered: false,
    },
    {
      id: 6,
      text: "Birthday event",
      organiser: "snaksk",
      location: "sss",
      date: "abcd",
      description: "sss",
      registered: false,
    },
    {
      id: 7,
      text: "Birthday event",
      organiser: "snaksk",
      location: "sss",
      date: "abcd",
      description: "sss",
      registered: false,
    },
    {
      id: 8,
      text: "Birthday event",
      organiser: "snaksk",
      location: "sss",
      date: "abcd",
      description: "sss",
      registered: false,
    },
    {
      id: 9,
      text: "Birthday event",
      organiser: "snaksk",
      location: "sss",
      date: "abcd",
      description: "sss",
      registered: false,
    },
    {
      id: 10,
      text: "Birthday event",
      organiser: "snaksk",
      location: "sss",
      date: "abcd",
      description: "sss",
      registered: false,
    },
    {
      id: 11,
      text: "Birthday event",
      organiser: "snaksk",
      location: "sss",
      date: "abcd",
      description: "sss",
      registered: false,
    },
    {
      id: 12,
      text: "Birthday event",
      organiser: "snaksk",
      location: "sss",
      date: "abcd",
      description: "sss",
      registered: false,
    },
  ]);

  const addEvent = (eve) => {
    const id = Math.floor(Math.random() * 10000) + 1;
    const registered = true;
    const newEvent = { id, ...eve, registered };
    setEvents([...events, newEvent]);
    console.log(newEvent);
  };

  const deleteEvent = (id) => {
    setEvents(events.filter((eve) => eve.id !== id));
  };

  const toggleRegistered = (id) => {
    setEvents(
      events.map((eve) =>
        eve.id === id ? { ...eve, registered: !eve.registered } : eve
      )
    );
  };

  return (
    <main>
      <Router>
        <Navbar collapseOnSelect expand="lg" bg="dark" variant="dark">
          <Navbar.Brand>Event Manager</Navbar.Brand>
          <Navbar.Toggle aria-controls="responsive-navbar-nav" />
          <Navbar.Collapse id="responsive-navbar-nav">
            <Nav className="mr-auto">
              <Nav.Link>
                {" "}
                <Link to="/">All Events</Link>
              </Nav.Link>
              <Nav.Link>
                {" "}
                <Link to="/myevents">My events</Link>
              </Nav.Link>
              <NavDropdown title="Events" id="collasible-nav-dropdown">
                <NavDropdown.Item>
                  {" "}
                  <Link to="/pastevents">Past Events</Link>
                </NavDropdown.Item>
                <NavDropdown.Item>
                  {" "}
                  <Link to="/upcomingevents">Upcoming Events</Link>
                </NavDropdown.Item>
              </NavDropdown>
            </Nav>
          </Navbar.Collapse>
        </Navbar>
      </Router>

      <Switch>
        <Route path="/" exact>
          <div className="container">
            <Header
              title="All Events"
              onCreateButton={() => setShowAddEvent(!showAddEvent)}
              showAddEvent={showAddEvent}
            />

            {showAddEvent && <AddEvent onAdd={addEvent} />}

            {events.length > 0 ? (
              <Events
                className="events"
                events={events}
                onDelete={deleteEvent}
                onToggle={toggleRegistered}
              />
            ) : (
              "No Events to show. Create an event using the CREATE EVENT BUTTON"
            )}
          </div>
        </Route>

        <Route path="/myevents">
          <MyEvents />
        </Route>
        <Route path="/pastevents">
          <PastEvent />
        </Route>
        <Route path="upcomingevents">
          <UpcomingEvent />
        </Route>
      </Switch>
    </main>

    // <div>

    // </div>
  );
}

export default App;
