import Header from "./components/Header";
import Events from "./components/Events";
import { useState } from "react";

function App() {
  const [events, setEvents] = useState([
    {
      id: 1,
      text: "Birthday event",
      time: "abcd",
      registered: false,
    },
    {
      id: 2,
      text: "Anniversary event",
      time: "abcd",
      registered: true,
    },
    {
      id: 3,
      text: "Birthday event",
      time: "abcd",
      registered: false,
    },
    {
      id: 4,
      text: "Birthday event",
      time: "abcd",
      registered: false,
    },
    {
      id: 5,
      text: "Birthday event",
      time: "abcd",
      registered: false,
    },
    {
      id: 6,
      text: "Birthday event",
      time: "abcd",
      registered: false,
    },
    {
      id: 7,
      text: "Birthday event",
      time: "abcd",
      registered: false,
    },
    {
      id: 8,
      text: "Birthday event",
      time: "abcd",
      registered: false,
    },
    {
      id: 9,
      text: "Birthday event",
      time: "abcd",
      registered: false,
    },
    {
      id: 10,
      text: "Birthday event",
      time: "abcd",
      registered: false,
    },{
      id: 11,
      text: "Birthday event",
      time: "abcd",
      registered: false,
    },{
      id: 12,
      text: "Birthday event",
      time: "abcd",
      registered: false,
    },
  ]);

  const deleteEvent = (id) => {
    setEvents(events.filter((eve) => eve.id !== id))
  }

  const toggleRegistered = (id) => {
    setEvents(events.map((eve) => {
      eve.id === id ? {...eve, registered: !eve.registered} : eve
    }))
  }

  return (
    <div className="container">
      <Header />
      {events.length>0 ? <Events className="events" events={events} onDelete={deleteEvent} /> : "No Events to show. Create an event using the CREATE EVENT BUTTON"}
    </div>
  );
}

export default App;
