import Header from "./components/Header";
import Events from "./components/Events";
import { useState } from "react";
import { Nav, Navbar, NavDropdown, NavLink } from "react-bootstrap";
import { Link } from 'react-router-dom';

function App() {
  const [events, setEvents] = useState([
    {
      id: 1,
      text: "Birthday event",
      time: "abcd",
      registered: false,
    },
    {
      id: 2,
      text: "Anniversary event",
      time: "abcd",
      registered: true,
    },
    {
      id: 3,
      text: "Birthday event",
      time: "abcd",
      registered: false,
    },
    {
      id: 4,
      text: "Birthday event",
      time: "abcd",
      registered: false,
    },
    {
      id: 5,
      text: "Birthday event",
      time: "abcd",
      registered: false,
    },
    {
      id: 6,
      text: "Birthday event",
      time: "abcd",
      registered: false,
    },
    {
      id: 7,
      text: "Birthday event",
      time: "abcd",
      registered: false,
    },
    {
      id: 8,
      text: "Birthday event",
      time: "abcd",
      registered: false,
    },
    {
      id: 9,
      text: "Birthday event",
      time: "abcd",
      registered: false,
    },
    {
      id: 10,
      text: "Birthday event",
      time: "abcd",
      registered: false,
    },
    {
      id: 11,
      text: "Birthday event",
      time: "abcd",
      registered: false,
    },
    {
      id: 12,
      text: "Birthday event",
      time: "abcd",
      registered: false,
    },
  ]);

  const deleteEvent = (id) => {
    setEvents(events.filter((eve) => eve.id !== id));
  };

  const toggleRegistered = (id) => {
    setEvents(
      events.map((eve) =>
        eve.id === id ? { ...eve, registered: !eve.registered } : eve
      )
    );
  };

  return (
    <div>
      <Navbar
      style={{ fontWeight: "300", fontFamily: "Montserrat" }}
      bg="light"
      expand="lg"
    >
      <Navbar.Brand style={{ fontWeight: "500" }}>Event Manager</Navbar.Brand>
      <Navbar.Toggle aria-controls="basic-navbar-nav" />
      <Navbar.Collapse id="basic-navbar-nav">
        <Nav className="mr-auto">
          <Nav.Link>
            <Link to="/">Home</Link>
          </Nav.Link>
          <Nav.Link>
            <Link to="/addevent">Add Event</Link>
          </Nav.Link>
          <NavDropdown title="Events" id="basic-nav-dropdown">
            <NavDropdown.Item>
              <Link to="/pastevents">Past Events</Link>
            </NavDropdown.Item>
            <NavDropdown.Item>
              <Link to="/upcomingevents">Upcoming Events</Link>
            </NavDropdown.Item>
          </NavDropdown>
        </Nav>
      </Navbar.Collapse>
    </Navbar>
      <div className="container">
        <Header />
        {events.length > 0 ? (
          <Events
            className="events"
            events={events}
            onDelete={deleteEvent}
            onToggle={toggleRegistered}
          />
        ) : (
          "No Events to show. Create an event using the CREATE EVENT BUTTON"
        )}
      </div>
    </div>
  );
}

export default App;
