import { useState } from "react";
import { Form, FormControl, FormGroup, Button } from "react-bootstrap";

const AddEvent = ({ onAdd }) => {
  const [text, setText] = useState("");
  const [organiser, setOrganiser] = useState("");
  const [date, setDate] = useState("");
  const [location, setLocation] = useState("");
  const [description, setDescription] = useState("");

  const onSubmit = (e) => {
    e.preventDefault();

    onAdd({text, organiser, date, location, description})

    setText('');
    setOrganiser('');
    setDate('');
    setLocation('');
    setDescription('');
  };

  return (
    <Form onSubmit={onSubmit}>
      <div>
        <Form.Label>Event Name</Form.Label>
        <Form.Control
          type="text"
          placeholder="Add event name"
          required="true"
          value={text}
          onChange={(e) => setText(e.target.value)}
        />
      </div>
      <div>
        <Form.Label>Event Organisers</Form.Label>
        <Form.Control
          type="text"
          placeholder="Who is organising this event?"
          required="true"
          value={organiser}
          onChange={(e) => setOrganiser(e.target.value)}
        />
      </div>
      <div>
        <Form.Label>Date</Form.Label>
        <Form.Control
          type="date" required pattern="\d{4}-\d{2}-\d{2}"
          placeholder="dd/mm/yyyy"
          required="true"
          value={date}
          onChange={(e) => setDate(e.target.value)}
        />
      </div>
      <div>
        <Form.Label>Location</Form.Label>
        <Form.Control
          type="text"
          placeholder="Where is the event being held?"
          required="true"
          value={location}
          onChange={(e) => setLocation(e.target.value)}
        />
      </div>
      <div>
        <Form.Label>Describe the Event</Form.Label>
        <Form.Control
          type="text"
          placeholder="Description(Optional)"
          required="false"
          value={description}
          onChange={(e) => setDescription(e.target.value)}
        />
      </div>

      <Button variant="primary" size="lg" block type="submit">
        Submit
      </Button>
    </Form>
  );
};

export default AddEvent;
