import Header from "./components/Header";
import Events from "./components/Events";
import { useState } from "react";
import { Nav, Navbar, NavDropdown, NavLink } from "react-bootstrap";
import "bootstrap/dist/css/bootstrap.min.css";
import { Link } from "react-router-dom";

function App() {
  const [events, setEvents] = useState([
    {
      id: 1,
      text: "Birthday event",
      time: "abcd",
      registered: false,
    },
    {
      id: 2,
      text: "Anniversary event",
      time: "abcd",
      registered: true,
    },
    {
      id: 3,
      text: "Birthday event",
      time: "abcd",
      registered: false,
    },
    {
      id: 4,
      text: "Birthday event",
      time: "abcd",
      registered: false,
    },
    {
      id: 5,
      text: "Birthday event",
      time: "abcd",
      registered: false,
    },
    {
      id: 6,
      text: "Birthday event",
      time: "abcd",
      registered: false,
    },
    {
      id: 7,
      text: "Birthday event",
      time: "abcd",
      registered: false,
    },
    {
      id: 8,
      text: "Birthday event",
      time: "abcd",
      registered: false,
    },
    {
      id: 9,
      text: "Birthday event",
      time: "abcd",
      registered: false,
    },
    {
      id: 10,
      text: "Birthday event",
      time: "abcd",
      registered: false,
    },
    {
      id: 11,
      text: "Birthday event",
      time: "abcd",
      registered: false,
    },
    {
      id: 12,
      text: "Birthday event",
      time: "abcd",
      registered: false,
    },
  ]);

  const deleteEvent = (id) => {
    setEvents(events.filter((eve) => eve.id !== id));
  };

  const toggleRegistered = (id) => {
    setEvents(
      events.map((eve) =>
        eve.id === id ? { ...eve, registered: !eve.registered } : eve
      )
    );
  };

  return (
    <div>
      <Navbar collapseOnSelect expand="lg" bg="dark" variant="dark">
        <Navbar.Brand href="#home">Event Manager</Navbar.Brand>
        <Navbar.Toggle aria-controls="responsive-navbar-nav" />
        <Navbar.Collapse id="responsive-navbar-nav">
          <Nav className="mr-auto">
            <Nav.Link href="#features">Features</Nav.Link>
            <Nav.Link href="#pricing">Pricing</Nav.Link>
            <NavDropdown title="Dropdown" id="collasible-nav-dropdown">
              <NavDropdown.Item href="#action/3.1">Action</NavDropdown.Item>
              <NavDropdown.Item href="#action/3.2">
                Another action
              </NavDropdown.Item>
              <NavDropdown.Item href="#action/3.3">Something</NavDropdown.Item>
              <NavDropdown.Divider />
              <NavDropdown.Item href="#action/3.4">
                Separated link
              </NavDropdown.Item>
            </NavDropdown>
          </Nav>
          <Nav>
            <Nav.Link href="#deets">More deets</Nav.Link>
            <Nav.Link eventKey={2} href="#memes">
              Dank memes
            </Nav.Link>
          </Nav>
        </Navbar.Collapse>
      </Navbar>
      <div className="container">
        <Header />
        {events.length > 0 ? (
          <Events
            className="events"
            events={events}
            onDelete={deleteEvent}
            onToggle={toggleRegistered}
          />
        ) : (
          "No Events to show. Create an event using the CREATE EVENT BUTTON"
        )}
      </div>
    </div>
  );
}

export default App;
